export function indented_tree_layout(tree) {
    // 计算行号, 从 0 开始 
    let index = 0
    // let paper_index = 0

    function dfs(tree) {
        tree['index'] = index
        for (let child of tree['children']) {
            index = index + 1
            dfs(child)
        }
    }

    dfs(tree)
}
